import React, { useEffect } from "react";
import axios from "axios";

const data = [
    {
        key: 1,
        title: "Feed My Hungry Children",
        content: "Make the world better, kinder, brighter. Every coin you give will create real change. 100 rs will feed 5 hungry children here in the US, but $1,000 will feed 50,000 starving children overseas. Be the good in the lives of many by giving right now.",
        amount: 5000,
    },
    {
        key: 2,
        title: "GiveIndia Campaign",
        content: "The COVID-19 pandemic is a case in point, and GiveIndia’s campaign to help vaccinate the country’s citizens succeeds on this basis.",
        amount: 2000,
    },
    {
        key: 3,
        title: "Help Gamma",
        content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad aliquip ex ea commodo consequat. Duis aute irure dolor in",
        amount: 3000,
    },
    {
        key: 4,
        title: "Help Delta",
        content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad aliquip ex ea commodo consequat. Duis aute irure dolor in",
        amount: 4000,
    },
];

// useEffect(() => {
//     axios
//         .get("http://localhost:4000/admin")
//         .then((res) => {
//             this.setState({ data: res.data });
//         })
//         .catch((error) => {
//             console.log(error);
//         });
// }, []);

function updation() {
    axios
        .get("http://localhost:4000/admin")
        .then((res) => {
            this.setState({ data: res.data });
        })
        .catch((error) => {
            console.log(error);
        });
}

updation();

console.log(data);

export default data;
