import React, { useState, useEffect } from "react";
import axios from "axios";
import { load } from "@cashfreepayments/cashfree-js";

function PaymentForm() {
  const [orderId, setOrderId] = useState("");
  const [cashfree, setCashfree] = useState(null);

  useEffect(() => {
    const initializeSDK = async () => {
      try {
        const sdk = await load({
          mode: "sandbox", // Set this to 'production' in live mode
        });
        setCashfree(sdk);
      } catch (error) {
        console.log("Cashfree SDK Initialization Failed:", error);
      }
    };

    initializeSDK();
  }, []);

  const getSessionId = async () => {
    try {
      const res = await axios.get("http://localhost:4000/payment");
      if (res.data && res.data.payment_session_id) {
        console.log(res.data);
        setOrderId(res.data.order_id);
        return res.data.payment_session_id;
      }
    } catch (error) {
      console.log("Error fetching payment session ID:", error);
    }
  };

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      if (!cashfree) {
        console.log("Cashfree SDK not loaded yet");
        return;
      }

      const sessionId = await getSessionId();
      if (!sessionId) {
        console.log("Session ID not found");
        return;
      }

      const checkoutOptions = {
        paymentSessionId: sessionId,
        redirectTarget: "_modal", // or "_self" to redirect after payment
      };

      cashfree.checkout(checkoutOptions).then((res) => {
        console.log("Payment Initialized", res);
      }).catch((error) => {
        console.log("Payment Initialization Error:", error);
      });
    } catch (error) {
      console.log("Error during payment process:", error);
    }
  };

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form onSubmit={handleClick}>
        {/* Form fields (if required) */}
        <button type="submit">Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
