import React, { useState, useEffect } from "react";
import { Route, BrowserRouter as Router, Switch, Redirect } from "react-router-dom";
import Home from "./Home";
import About from "./About";
import Campaigns from "./Campaigns";
import Contact from "./Contact";
import Navbar from "./Navbar";
import Cards from "./Card";
import Admin from "./Admin";
import Login from "./Login";
import UpdateCampaign from "./UpdateCampaign";
import ProtectedRoute from "./ProtectedRoute";
import "../AppStyle.css";
import PaymentForm from "./PaymentForm";
import AllCampaigns from "./AllCampaigns";
import Signup from "./Signup";

function App() {
  const [checkLogin, setCheckLogin] = useState(null);

  useEffect(() => {
    try {
      const loggedInUser = localStorage.getItem("user");
      if (loggedInUser) {
        const foundUser = JSON.parse(loggedInUser);
        setCheckLogin(foundUser);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <Router>
      {console.log("App login state:", checkLogin)}
      <Navbar />
      <Switch>
        {/* Public Routes */}
        <Route exact path="/" component={Home} />
        <Route exact path="/about" component={About} />
        <Route exact path="/campaigns" component={Campaigns} />
        <Route exact path="/contact" component={Contact} />
        <Route exact path="/card" component={Cards} />

        {/* Role Selection Route */}
        <Route exact path="/role-select" render={() => <RoleSelect />} />

        {/* Auth Routes */}
        <Route
          exact
          path="/admin/login"
          render={() => (checkLogin && checkLogin.role === 'admin' ? <Redirect to="/admin" /> : <Login role="admin" />)}
        />
        <Route
          exact
          path="/user/login"
          render={() => (checkLogin && checkLogin.role === 'user' ? <Redirect to="/" /> : <Login role="user" />)}
        />
        
        {/* Campaigns and Admin Routes */}
        <Route exact path="/AllCampaigns" component={AllCampaigns} />
        <ProtectedRoute exact path="/updatecampaign" component={UpdateCampaign} />

        {/* Admin Protected Route */}
        <ProtectedRoute
          exact
          path="/admin"
          component={Admin}
          checkLogin={checkLogin}
          role="admin"
        />

        {/* Payment Route */}
        <Route
          exact
          path="/paymentform"
          render={(props) => <PaymentForm {...props} />}
        />

        {/* Redirect to login page if user is not logged in */}
        <Redirect to={checkLogin ? "/admin" : "/login"} />
      </Switch>
    </Router>
  );
}

function RoleSelect() {
  return (
    <div>
      <h2>Select your role</h2>
      <button onClick={() => window.location.href = '/admin/login'}>Admin Login</button>
      <button onClick={() => window.location.href = '/user/login'}>User Login</button>
    </div>
  );
}

export default App;
