function Campaigns(props) {
  const [data, setData] = useState([]);
  const navigate = useNavigate(); // useNavigate for React Router v6

  useEffect(() => {
    axios
      .get("http://localhost:4000/admin")
      .then((res) => {
        setData(res.data); // assuming res.data is an array of campaign objects
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <div className="Carousel">
      <section>
        <h1 className="campaigns-heading" style={{ margin: "1% 0% .5% 4%" }}>
          <b>ONGOING CAMPAIGNS</b>
        </h1>

        <Link to="/AllCampaigns" className="link">
          <button type="button" className="btn btn-success btn-lg">
            MORE CAMPAIGNS
          </button>
        </Link>
      </section>

      <div id="campaigns" style={{ backgroundColor: "grey" }}>
        <div id="testimonial-carousal" className="carousel slide" data-ride="false">
          <div className="carousel-inner">
            {/* First Slide */}
            <div className="carousel-item active">
              <h1 className="campaign-firstslide">
                <b>BE A PROUD DONOR</b>
              </h1>
            </div>

            {/* Mapping through campaigns and creating carousel items */}
            {data.map((info, index) => (
              <div className="carousel-item" key={index}>
                <div id="campaigns" className="twocolor-campaign campaign-content">
                  <img
                    className="campaign-img"
                    src={require("../images/about-img.jpg").default}
                    alt={`Campaign image for ${info.title}`} // Adding alt text for accessibility
                  />

                  <div>
                    <div className="campaign-info">
                      <h3>
                        <b>{info.title}</b>
                      </h3>
                      <p>{info.content.slice(0, 150)}...</p> {/* Slice content to show a preview */}
                    </div>

                    <div className="campaign-amount-button">
                      <div className="campaign-amount">
                        <b>REQUIRED AMOUNT: Rs. {info.amount}</b>
                      </div>

                      <button
                        type="button"
                        className="btn btn-success btn-lg"
                        onClick={() => {
                          navigate("/card", {
                            state: {
                              title: info.title,
                              content: info.content,
                              amount: info.amount,
                            },
                          });
                        }}
                      >
                        DONATE NOW
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Carousel Controls */}
          <a
            className="carousel-control-prev"
            href="#testimonial-carousal"
            role="button"
            data-slide="prev"
          >
            <span className="carousel-control-prev-icon"></span>
          </a>

          <a
            className="carousel-control-next"
            href="#testimonial-carousal"
            role="button"
            data-slide="next"
          >
            <span className="carousel-control-next-icon"></span>
          </a>
        </div>
      </div>
    </div>
  );
}

export default Campaigns;
