import React, { useEffect, useState } from "react";
import { useLocation, useHistory } from 'react-router-dom';
import axios from "axios";

function Card(props) {
  const { title, content, amount } = props;
  const history = useHistory();
  const location = useLocation();

  const [donorInfo, setDonorInfo] = useState({
    donor: [],
    donation: 0,
    amount: 0,
    buttonStatus: false,
  });

  useEffect(() => {
    const info = {
      title: location.state.title,
      content: location.state.content,
      amount: location.state.amount
    };

    axios
      .post("http://localhost:4000/donorinfo", info)
      .then((res) => {
        setDonorInfo(res.data);
      })
      .catch((error) => console.log(error));
  }, [location.state.title, location.state.content]);

  useEffect(() => {
    if (donorInfo && donorInfo.donor) {
      donorInfo.donor.forEach(element => {
        console.log(element.donatedmoney);
      });
    }
  }, [donorInfo]);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="Card-page">
      <h2 style={{ margin: "1% 0% -1% 40%" }}><b>DONATION PROGRESS BAR</b></h2>
      <section className="progress-bar">
        <div className="progress" style={{ height: "70px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            style={{ width: (donorInfo.donation / donorInfo.amount) * 100 + "%" }}
            aria-valuenow={donorInfo.donation}
            aria-valuemin="0"
            aria-valuemax="100"
          >
            <h5>
              {(donorInfo.donation > 0) ? ((donorInfo.donation) / (donorInfo.amount) * 100).toFixed(2) + "%" + " [ Rs." + donorInfo.donation + " ]" : "0%"}
            </h5>
          </div>
        </div>
      </section>

      <section>
        <div id="cards" className="twocolor-card card-content">
          <img
            className="card-img"
            src={require("../images/about-img.jpg").default}
            alt="logo"
          />
          <div>
            <div className="card-info">
              <h1><b>{location.state.title}</b></h1>
              <h4>{location.state.content}</h4>
            </div>

            <div className="card-amount-button">
              <div className="card-amount">
                <h2><b>REQUIRED AMOUNT: Rs.{location.state.amount}</b></h2>
              </div>

              <button type="button" className="btn btn-success btn-lg"
                onClick={() => history.push('/')}>
                BACK
              </button>

              <button type="button" className="btn btn-success btn-lg"
                disabled={donorInfo.donation >= donorInfo.amount || donorInfo.buttonStatus}
                onClick={() => {
                  history.push({
                    pathname: '/paymentform',
                    state: {
                      title: location.state.title,
                      content: location.state.content
                      
                    },
                  });
                }}
              >
                DONATE NOW
              </button>
            </div>
          </div>

        </div>
      </section>

      <section className="card-donorlist">
        <table className="table table-hover table-dark">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col" colspan="2">PROUD DONORS TRANSACTION ID</th>
              <th scope="col">AMOUNT(Rs.)</th>
              <th scope="col">TRANSACTION STATUS</th>
            </tr>
          </thead>
          <tbody>
            {donorInfo.donor.length > 0 ?
              donorInfo.donor.map((info, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td colspan="2">{info.id}</td>
                  <td>{info.donatedmoney}</td>
                  <td>{info.status}</td>
                </tr>
              )) :
              <tr><td colSpan="5">No donors yet</td></tr>
            }
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default Card;





