import React, { useState } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

function PaymentForm() {
  const location = useLocation();

  const [note, setNote] = useState({
    name: "",
    email: "",
    phone: "",
    amount: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  }

  async function submitData(event) {
    event.preventDefault();

    // Add title and content from location.state
    const data = {
      ...note,
      title: location.state?.title || "",
      content: location.state?.content || "",
    };

    try {
      // Make POST request to create Razorpay order
      const response = await axios.post("http://localhost:4000/paynow", data);
      const { orderId, amount, currency, key } = response.data;

      // Initialize Razorpay checkout
      const options = {
        key, // Key provided by Razorpay
        amount: amount.toString(), // Amount in paisa
        currency,
        name: "Donation",
        description: "Support our cause",
        order_id: orderId, // Razorpay order ID
        handler: function (response) {
          // Handle payment success
          console.log("Payment successful!", response);
          alert("Payment successful! Thank you for your donation.");
        },
        prefill: {
          name: note.name,
          email: note.email,
          contact: note.phone,
        },
        theme: {
          color: "#3399cc",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (error) {
      console.error("Payment error:", error);
      alert("Something went wrong. Please try again later.");
    }
  }

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form onSubmit={submitData}>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
          required
        />
        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="email"
          required
        />
        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="tel"
          required
        />
        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="number"
          required
        />
        <button type="submit">Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
