import React, { useState } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

function PaymentForm() {
  const location = useLocation();
  const [note, setNote] = useState({
    name: "",
    email: "",
    phone: "",
    amount: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  }

  async function submitData(event) {
    event.preventDefault();

    try {
      // Prepare payload with title and content from location state
      const payload = {
        ...note,
        title: location.state?.title || "Default Title",
        content: location.state?.content || "Default Content",
      };

      // Send request to create order via backend
      const { data } = await axios.post("http://localhost:4000/create-order", payload);

      // Redirect to Cashfree payment page
      const cashfreeForm = document.createElement("form");
      cashfreeForm.action = data.payment_link;
      cashfreeForm.method = "POST";
      cashfreeForm.target = "_self";
      document.body.appendChild(cashfreeForm);
      cashfreeForm.submit();
    } catch (error) {
      console.error("Error in payment process:", error);
      alert("Payment initiation failed. Please try again.");
    }
  }

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form onSubmit={submitData}>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
          required
        />
        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="email"
          required
        />
        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="tel"
          required
        />
        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="number"
          required
        />
        <button type="submit">Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
