import React, { useState, useEffect } from "react";
import { Route, BrowserRouter as Router, Switch, Redirect } from "react-router-dom";
import Navbar from "./Navbar";
import Login from "./Login";
import Signup from "./Signup";
import Admin from "./Admin";
import ProtectedRoute from "./ProtectedRoute";
import "../AppStyle.css";

function App() {
  const [checkLogin, setCheckLogin] = useState(null);

  useEffect(() => {
    try {
      const loggedInUser = localStorage.getItem("user");
      if (loggedInUser) {
        const foundUser = JSON.parse(loggedInUser);
        setCheckLogin(foundUser);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <Router>
      {console.log("App login state:", checkLogin)}
      <Navbar />
      <Switch>
        <Route exact path="/" render={() => (checkLogin ? <Redirect to="/admin" /> : <Login />)} />
        <Route exact path="/signup" render={() => (checkLogin ? <Redirect to="/admin" /> : <Signup />)} />
        
        <ProtectedRoute exact path="/admin" component={Admin} />
        
        {/* Other routes like /campaigns, /about, etc. */}
      </Switch>
    </Router>
  );
}

export default App;
