import React, { useState, useEffect } from "react";
import { Route, BrowserRouter as Router, Switch, Redirect } from "react-router-dom";
import Home from "./Home";
import About from "./About";
import Campaigns from "./Campaigns";
import Contact from "./Contact";
import Navbar from "./Navbar";
import Cards from "./Card";
import Admin from "./Admin";
import Login from "./Login";
import UpdateCampaign from "./UpdateCampaign";
import ProtectedRoute from "./ProtectedRoute";
import "../AppStyle.css";
import PaymentForm from "./PaymentForm";
import AllCampaigns from "./AllCampaigns";
import Signup from "./Signup";

function App() {
  const [checkLogin, setCheckLogin] = useState(null);

  useEffect(() => {
    try {
      const loggedInUser = localStorage.getItem("user");
      if (loggedInUser) {
        const foundUser = JSON.parse(loggedInUser);
        setCheckLogin(foundUser);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);

  const handleLogin = (userCredentials) => {
    // Here you would send the userCredentials to the backend for verification
    // Assuming the backend returns a user object on successful login
    fetch("/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userCredentials),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          localStorage.setItem("user", JSON.stringify(data.user));
          setCheckLogin(data.user);
        } else {
          alert("Invalid login credentials");
        }
      })
      .catch((error) => console.error("Error logging in:", error));
  };

  return (
    <Router>
      <Navbar />
      <Switch>
        {/* Public Routes */}
        <Route exact path="/" render={() => (checkLogin ? <Redirect to="/home" /> : <Login handleLogin={handleLogin} />)} />
        <Route exact path="/about" component={About} />
        <Route exact path="/campaigns" component={Campaigns} />
        <Route exact path="/contact" component={Contact} />
        <Route exact path="/card" component={Cards} />
        
        {/* Auth Routes */}
        <Route
          exact
          path="/login"
          render={() => (checkLogin ? <Redirect to="/home" /> : <Login handleLogin={handleLogin} />)}
        />
        <Route
          exact
          path="/signup"
          render={() => (checkLogin ? <Redirect to="/home" /> : <Signup />)}
        />

        {/* Campaigns and Admin Routes */}
        <Route exact path="/AllCampaigns" component={AllCampaigns} />
        <ProtectedRoute exact path="/updatecampaign" component={UpdateCampaign} />
        
        {/* Admin Protected Route */}
        <ProtectedRoute exact path="/admin" component={Admin} />
        
        {/* Payment Route */}
        <Route
          exact
          path="/paymentform"
          render={(props) => <PaymentForm {...props} />}
        />

        {/* Redirect to home if no route matches */}
        <Redirect to="/" />
      </Switch>
    </Router>
  );
}

export default App;
