import React, { useEffect, useState } from "react";
import { useLocation, useHistory } from 'react-router-dom';
import axios from "axios";

function Card(props) {
  const { title, content, amount } = props;
  const history = useHistory();
  const location = useLocation();

  // Initialize donorInfo with default values
  const [donorInfo, setDonorInfo] = useState({
    donor: [],  // Ensure donor is an empty array initially
    donation: 0,
    amount: 0,
    buttonStatus: false,
  });

  useEffect(() => {
    // Identification data of clicked campaign
    const info = {
      title: location.state.title,
      content: location.state.content,
    };

    // Fetch donor info from the server
    axios
      .post("http://localhost:4000/donorinfo", info)
      .then((res) => {
        setDonorInfo(res.data);
      })
      .catch((error) => console.log(error));
  }, [location.state.title, location.state.content]);

  useEffect(() => {
    if (donorInfo && donorInfo.donor) {
      donorInfo.donor.forEach((element) => {
        console.log(element.donatedmoney);
      });
    }
  }, [donorInfo]);

  // Scroll to top on page load
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="Card-page">
      {/* PROGRESS BAR */}
      <h2 style={{ margin: "1% 0% -1% 40%" }}>
        <b>DONATION PROGRESS BAR</b>
      </h2>
      <section className="progress-bar">
        <div className="progress" style={{ height: "70px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            style={{
              width: donorInfo.donation && donorInfo.amount
                ? (donorInfo.donation / donorInfo.amount) * 100
                : 0 + "%",
            }}
            aria-valuenow="25"
            aria-valuemin="0"
            aria-valuemax="100"
          >
            <h5>
              {/* Percentage of progress in bar */}
              {donorInfo.donation > 0
                ? ((donorInfo.donation / donorInfo.amount) * 100).toFixed(2) +
                  "%" +
                  " [ Rs." +
                  donorInfo.donation +
                  " ]"
                : "0%"}
            </h5>
          </div>
        </div>
      </section>

      {/* NEEDY PERSON INFORMATION CONTAINER */}
      <section>
        <div id="cards" className="twocolor-card card-content">
          <img
            className="card-img"
            src={require("../images/about-img.jpg").default}
            alt="logo"
          />

          <div>
            <div className="card-info">
              <h1>
                <b>{location.state.title}</b>
              </h1>

              <h4>{location.state.content}</h4>
            </div>

            <div className="card-amount-button">
              <div className="card-amount">
                <h2>
                  <b>REQUIRED AMOUNT: Rs.{location.state.amount}</b>
                </h2>
              </div>

              <br />

              {/* BACK BUTTON */}
              <button
                type="button"
                className="btn btn-success btn-lg"
                onClick={() => {
                  history.push({
                    pathname: "/",
                    state: {}, // location state
                  });
                }}
              >
                BACK
              </button>

              {/* DONATE NOW BUTTON */}
              <button
                type="button"
                className="btn btn-success btn-lg"
                disabled={
                  donorInfo.donation >= donorInfo.amount || donorInfo.buttonStatus
                    ? true
                    : false
                }
                onClick={() => {
                  history.push({
                    pathname: "/paymentform",
                    state: {
                      title: location.state.title,
                      content: location.state.content,
                    },
                  });
                }}
              >
                DONATE NOW
              </button>
            </div>
          </div>

          {/* Alert for campaign status */}
          {donorInfo.donation >= donorInfo.amount || donorInfo.buttonStatus
            ? alert("This campaign is either fulfilled or stopped due to some reasons")
            : null}
        </div>
      </section>

      {/* Donors List */}
      <section className="card-donorlist">
        <table className="table table-hover table-dark">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col" colspan="2">
                PROUD DONORS TRANSACTION ID
              </th>
              <th scope="col">AMOUNT(Rs.)</th>
              <th scope="col">TRANSACTION STATUS</th>
            </tr>
          </thead>

          {/* Render donors dynamically */}
          {donorInfo.donor && donorInfo.donor.length > 0 ? (
            donorInfo.donor.map((info, index) => (
              <tbody key={index}>
                <tr>
                  <th scope="row">{index + 1}</th>
                  <td colspan="2">
                    {info.id.slice(0, info.id.length - 4) + "xxxx"}
                  </td>
                  <td>{info.donatedmoney}</td>
                  <td>{info.status}</td>
                </tr>
              </tbody>
            ))
          ) : (
            <tbody>
              <tr>
                <td colSpan="5">No donors found</td>
              </tr>
            </tbody>
          )}
        </table>
      </section>
    </div>
  );
}

export default Card;
