import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { withRouter } from "react-router-dom";  // Importing withRouter to access history

class Campaigns extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:4000/admin")
      .then((res) => {
        this.setState({ data: res.data });
        console.log("response", res);
      })
      .catch((err) => console.log(err));
  }

  render() {
    const { data } = this.state;
    return (
      <div>
        <div className="Carousel">
          <section>
            <h1 className="campaigns-heading" style={{ margin: "1% 0% .5% 4%" }}>
              <b>ONGOING CAMPAIGNS</b>
            </h1>

            <Link to="/AllCampaigns" className="link">
              <button type="button" className="btn btn-success btn-lg">
                MORE CAMPAIGNS
              </button>
            </Link>
          </section>

          <div id="campaigns" style={{ backgroundColor: "grey" }}>
            <div id="testimonial-carousal" className="carousel slide" data-ride="false">
              <div className="carousel-inner">
                <div className="carousel-item active">
                  <h1 className="campaign-firstslide">
                    <b>BE A PROUD DONOR</b>
                  </h1>
                </div>

                {/* Loop through data to generate carousel items */}
                {data.map((info) => (
                  <div className="carousel-item" key={info.key}>
                    <div id="campaigns" className="twocolor-campaign campaign-content">
                      <img
                        className="campaign-img"
                        src={require("../images/about-img.jpg").default}
                        alt="logo"
                      />

                      <div>
                        <div className="campaign-info">
                          <h3>
                            <b>{info.title}</b>
                          </h3>
                          <p>{info.content.slice(0, 150)}</p>
                        </div>

                        <div className="campaign-amount-button">
                          <div className="campaign-amount">
                            <b>REQUIRED AMOUNT: Rs.{info.amount}</b>
                          </div>

                          <button
                            type="button"
                            className="btn btn-success btn-lg"
                            onClick={() => {
                              this.props.history.push({
                                pathname: "/card",
                                state: {
                                  title: info.title,
                                  content: info.content,
                                  amount: info.amount,
                                },
                              });
                            }}
                          >
                            DONATE NOW
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              <a className="carousel-control-prev" href="#testimonial-carousal" role="button" data-slide="prev">
                <span className="carousel-control-prev-icon"></span>
              </a>

              <a className="carousel-control-next" href="#testimonial-carousal" role="button" data-slide="next">
                <span className="carousel-control-next-icon"></span>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Campaigns); // Wrap the class component with withRouter
