import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import data from "../data";

function AllCampaigns() {
  const [campdata, setcampdata] = useState(data);
  const history = useHistory();

  // Uncomment the useEffect and replace data with API fetch if needed
  // useEffect(() => {
  //   axios
  //     .get("http://localhost:4000/admin")
  //     .then((res) => {
  //       setcampdata(res.data); // Fixed the response field 'campdata' to 'data'
  //     })
  //     .catch(function (error) {
  //       console.log(error);
  //     });
  // }, []);

  return (
    <div>
      <h1 className="AllCampaigns-Title">
        <b>CAMPAIGNS</b>
      </h1>

      <section className="AllCampaigns-Card">
        {campdata.map((info, index) => (
          <div key={index} className="AllCampaigns-CardItem">
            <img
              className="campaign-img"
              src={require("../images/donation.jpg").default}
              alt="Campaign"
            />
            <div className="campaign-info">
              <h3><b>{info.title}</b></h3>
              <p>{info.content}</p>
            </div>

            <div className="AllCampaigns-amount-button">
              <div className="campaign-amount">
                <b>REQUIRED AMOUNT: Rs.{info.amount}</b>
              </div>
              <button
                type="button"
                className="btn btn-success btn-lg"
                onClick={() => {
                  history.push({
                    pathname: "/card",
                    state: {
                      title: info.title,
                      content: info.content,
                      amount: info.amount,
                    },
                  });
                }}
              >
                DONATE
              </button>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
}

export default AllCampaigns;
