import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";

function Login(props) {
  const [credential, setCredential] = useState({
    email: "",
    password: "",
  });

  const [data, setData] = useState({});
  const [user, setUser] = useState(null);
  const history = useHistory();

  // Check if user is already logged in (from localStorage)
  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setUser(foundUser);
      history.push('/admin');  // Redirect to admin page
    }
  }, [history]);

  // Handle input changes
  function handleChange(event) {
    const { name, value } = event.target;
    setCredential((prevCredential) => ({
      ...prevCredential,
      [name]: value,
    }));
  }

  // Handle form submission (login)
  function submitCredential(event) {
    event.preventDefault();

    axios.post("http://localhost:4000/login", credential)
      .then((res) => {
        if (res.data.result === true) {
          localStorage.setItem('user', JSON.stringify(res.data));
          setData(res.data);
          history.push('/admin');  // Navigate to the admin page
        } else {
          alert("Invalid credentials. Please try again.");
        }
      })
      .catch((error) => {
        console.log(error);
        alert("An error occurred. Please try again.");
      });

    setCredential({
      email: "",
      password: "",
    });
  }

  return (
    <div className="Login-page">
      <form>
        <label>Email</label>
        <input
          type="email"
          name="email"
          onChange={handleChange}
          value={credential.email}
        />
        <label>Password</label>
        <input
          type="password"
          name="password"
          onChange={handleChange}
          value={credential.password}
        />
        <button type="submit" onClick={submitCredential}>
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
