import React from "react";
import About from "./About";
import Campaigns from "./Campaigns";
import Contact from "./Contact";
import { Link } from "react-scroll";
import "../AppStyle.css";
import {useTypewriter, cursor} from 'react-simple-typewriter'
function Home() {
  const text=useTypewriter({
    words: ['...head']
  })
  return (
    <div id="home">
      <div className="home-content">
        <div className="note">
        <div className="caption">
        Put Your Hand
        </div>
        <div className ="quote">
        "To give is to invest in someone else's happiness."
        </div>
        </div>
        <Link
          to="campaigns"
          className="link"
          spy={true}
          smooth={true}
          activeClass="active"
          duration={1000}
        >
          <button type="button" class="btn btn-success btn-lg">
            DONATE NOW
          </button>
        </Link>
      </div>

      <About />

      <Campaigns />

      <Contact />
    </div>
  );
}

export default Home;
