import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
import axios from 'axios';
import { load } from '@cashfreepayments/cashfree-js';

function DonationForm() {
  const location = useLocation();
  const history = useHistory();

  // Redirect if state is missing
  useEffect(() => {
    if (!location.state) {
      alert('Campaign details are missing. Redirecting to campaigns page.');
      history.push('/allcampaigns'); // Replace with the actual campaigns page route
    }
  }, [location.state, history]);

  // State for user details
  const [note, setNote] = useState({
    name: '',
    email: '',
    phone: '',
    amount: '',
  });

  // Cashfree SDK instance
  let cashfree;

  useEffect(() => {
    const initializeSDK = async () => {
      cashfree = await load({
        mode: 'sandbox', // Change to 'production' for live payments
      });
    };
    initializeSDK();
  }, []);

  const [orderId, setOrderId] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNote((prev) => ({ ...prev, [name]: value }));
  };

  const getSessionId = async () => {
    try {
      const res = await axios.post('http://localhost:4000/payment', {
        name: note.name,
        email: note.email,
        phone: note.phone,
        amount: note.amount,
        title: location.state?.title,
        content: location.state?.content,
      });

      if (res.data && res.data.payment_session_id) {
        console.log(res.data);
        setOrderId(res.data.order_id);
        return res.data.payment_session_id;
      }
    } catch (error) {
      console.error('Error fetching session ID:', error);
    }
  };

  const verifyPayment = async () => {
    try {
      const res = await axios.post('http://localhost:4000/verify', {
        orderId,
      });

      if (res && res.data) {
        alert('Payment verified successfully!');
      }
    } catch (error) {
      console.error('Error verifying payment:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const sessionId = await getSessionId();
      const checkoutOptions = {
        paymentSessionId: sessionId,
        redirectTarget: '_modal', // Opens payment in a modal
      };

      cashfree.checkout(checkoutOptions).then(() => {
        console.log('Payment initialized');
        verifyPayment();
      });
    } catch (error) {
      console.error('Error during payment initialization:', error);
    }
  };

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
          required
        />

        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="email"
          required
        />

        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="tel"
          required
        />

        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="number"
          required
        />

        <input
          name="title"
          value={location.state?.title || ''}
          placeholder="Title"
          type="text"
          readOnly
        />

        <input
          name="content"
          value={location.state?.content || ''}
          placeholder="Content"
          type="text"
          readOnly
        />

        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
}

export default DonationForm;
