import React, { useState, useEffect } from "react";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom"; // Correct usage of Routes in React Router v6
import Home from "./Home";
import About from "./About";
import Campaigns from "./Campaigns";
import Contact from "./Contact";
import Navbar from "./Navbar";
import Cards from "./Card";
import Admin from "./Admin";
import Login from "./Login";
import UpdateCampaign from "./UpdateCampaign";
import ProtectedRoute from "./ProtectedRoute"; // Assuming ProtectedRoute is a custom component
import PaymentForm from "./PaymentForm";
import AllCampaigns from "./AllCampaigns";

import "../AppStyle.css";

function App() {
  const [checkLogin, setCheckLogin] = useState();

  useEffect(() => {
    try {
      const loggedInUser = localStorage.getItem("user");
      if (loggedInUser) {
        const foundUser = JSON.parse(loggedInUser);
        setCheckLogin(foundUser);
      }
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <Router>
      {console.log("app login", checkLogin)}

      <Navbar />

      <Routes>
        {/* Use Routes instead of Switch in React Router v6 */}
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/campaigns" element={<Campaigns />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/card" element={<Cards />} />
        <Route path="/login" element={<Login />} />
        <Route path="/AllCampaigns" element={<AllCampaigns />} />
        
        {/* Protected Routes, assuming ProtectedRoute is a custom component */}
        <Route path="/admin" element={<ProtectedRoute component={Admin} />} />
        <Route path="/updatecampaign" element={<ProtectedRoute component={UpdateCampaign} />} />
        <Route path="/paymentform" element={<PaymentForm />} />
      </Routes>
    </Router>
  );
}

export default App;
