import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

function Login() {
  const [credential, setCredential] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const history = useHistory();

  const adminCredentials = {
    email: "vignesh05@123",
    password: "vignesh123",
  };

  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      const foundUser = JSON.parse(loggedInUser);
      setCredential(foundUser);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredential((prevCredential) => ({
      ...prevCredential,
      [name]: value,
    }));
  };

  const submitCredential = async (e) => {
    e.preventDefault();
    // Check if the entered credentials match the admin credentials
    if (
      credential.email === adminCredentials.email &&
      credential.password === adminCredentials.password
    ) {
      localStorage.setItem('user', JSON.stringify(credential));
      setMessage("Admin login successful!");
      history.push('/admin'); // Redirect to admin page
    } else {
      localStorage.setItem('user', JSON.stringify(credential));
      setMessage("Login successful!");
      history.push('/'); // Redirect to home page
    }
  };

  return (
    <div className="Login-page">
      <form onSubmit={submitCredential}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={credential.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={credential.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default Login;
