import React, { useState } from 'react';
import CreateArea from './CreateArea';

function Admin({ addCampaign, campaigns = [], setCampaigns }) {  // Default to empty array
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const deleteCampaign = (id) => {
    setCampaigns((prevCampaigns) => prevCampaigns.filter(campaign => campaign._id !== id));
  };

  return (
    <div className="admin-container">
      <button type="button" className="btn btn-danger btn-lg adminLogout" onClick={handleLogout}>
        LOGOUT
      </button>
      <div className="admin-content">
        <h3 className="AdminAddCampaignTitle">ADD CAMPAIGN</h3>
        <div className="AdminAddCampaignForm Adminform">
          <CreateArea onAdd={addCampaign} />
        </div>
        <div className="campaign-list">
          <h4>Existing Campaigns</h4>
          {campaigns && campaigns.length > 0 ? (
            campaigns.map(campaign => (
              <div key={campaign._id} className="campaign-item">
                <h5>{campaign.title}</h5>
                <button onClick={() => deleteCampaign(campaign._id)} className="btn btn-danger">Delete</button>
              </div>
            ))
          ) : (
            <p>No campaigns available.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Admin;
