import React from "react";
import About from "./About";
import Campaigns from "./Campaigns";
import Contact from "./Contact";
import { Link } from "react-scroll";
import "../AppStyle.css";
import {useTypewriter, ursor} from 'react-simple-typewriter'
function Home() {
  const text=useTypewriter({
    words: ['heal...','feed...','educate...','empower...'],
    loop: {},
  })
  return (
    <div id="home">
      <div className="home-content">
        <div className="note">
        <h1 style={{margin: '50px'}}>
        Your gift can
        <span style={{fontweight: 'bold',color:'cyan'}}>
          {text}
        </span>
        <cursor/>
        </h1>
        </div>
        <Link
          to="campaigns"
          className="link"
          spy={true}
          smooth={true}
          activeClass="active"
          duration={1000}
        >
          <button type="button" class="btn btn-success btn-lg">
            DONATE NOW
          </button>
        </Link>
      </div>

      <About />

      <Campaigns />

      <Contact />
    </div>
  );
}

export default Home;
