import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

function Login() {
  const [credential, setCredential] = useState({ email: "", password: "" });
  const [user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const history = useHistory();

  // Handle input changes
  function handleChange(event) {
    const { name, value } = event.target;
    setCredential((prevCredential) => ({ ...prevCredential, [name]: value }));
  }

  // Submit credentials to the server
  async function submitCredential(event) {
    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/login", credential);
      if (response.data.result) {
        // Login success
        const loggedInUser = response.data;
        setUser(loggedInUser);
        localStorage.setItem("user", JSON.stringify(loggedInUser));
        history.push("/admin");
      } else {
        // Login failure
        setErrorMessage(response.data.message || "Invalid email or password. Please try again.");
      }
    } catch (error) {
      setErrorMessage(error.response?.data?.message || "An error occurred during login.");
    }
  }

  // Check for previously logged-in user
  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
    if (loggedInUser) {
      const parsedUser = JSON.parse(loggedInUser);
      setUser(parsedUser);
      history.push("/admin"); // Automatically redirect if logged in
    }
  }, [history]);

  return (
    <div className="Login-page">
      <h1>Login</h1>
      <form onSubmit={submitCredential}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            onChange={handleChange}
            value={credential.email}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            onChange={handleChange}
            value={credential.password}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
}

export default Login;
