import React, { useState } from "react";
import axios from "axios";
import { useLocation } from 'react-router-dom';

function PaymentForm() {
  const location = useLocation();
  const [note, setNote] = useState({
    name: "",
    email: "",
    phone: "",
    amount: ""
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setNote(prevNote => ({
      ...prevNote,
      [name]: value
    }));
  }

  function submitData(event) {
    event.preventDefault();

    note.title = location.state.title;
    note.content = location.state.content;

    axios
      .post("http://localhost:4000/create-order", note)
      .then((res) => {
        const options = {
          key: "KHmYZhobqR6hycRBtE3CYoUK", // Replace with your Razorpay key
          amount: res.data.amount, // Amount should be in paise (₹ 100 = 10000 paise)
          currency: "INR",
          name: note.name,
          description: note.content,
          order_id: res.data.order_id,
          handler: function (response) {
            // On success callback
            console.log("Payment successful:", response);
            // Optionally, send data to your server for verification
            axios.post("http://localhost:4000/payment-success", response);
          },
          prefill: {
            name: note.name,
            email: note.email,
            contact: note.phone,
          },
        };

        const rzp = new window.Razorpay(options);
        rzp.open();
      })
      .catch((error) => console.log(error));

    setNote({
      name: "",
      email: "",
      phone: "",
      amount: ""
    });
  }

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>

      <form onSubmit={submitData}>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
        />
        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="text"
        />
        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="text"
        />
        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="text"
        />
        <input
          name="title"
          onChange={handleChange}
          value={location.state.title}
          placeholder="Title"
          type="text"
        />
        <input
          name="content"
          onChange={handleChange}
          value={location.state.content}
          placeholder="Content"
          type="text"
        />
        <button type="submit">Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
