import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Campaigns from './Campaigns';
import Contact from './Contact';
import Cards from './Card';
import Signup from './Signup';
import Login from './Login';
import LandingPage from './LandingPage'; // Ensure this is imported
import AllCampaigns from './AllCampaigns';
import Admin from './Admin';
import UpdateCampaign from './UpdateCampaign';
import PaymentForm from './PaymentForm';
import ProtectedRoute from './ProtectedRoute';

function App() {
  const [campdata, setCampdata] = useState(() => {
    // Load campdata from localStorage if available
    const savedCampdata = localStorage.getItem('campdata');
    return savedCampdata ? JSON.parse(savedCampdata) : [];
  });

  useEffect(() => {
    // Save campdata to localStorage whenever it changes
    localStorage.setItem('campdata', JSON.stringify(campdata));
  }, [campdata]);

  const addCampaign = (newCampaign) => {
    setCampdata((prevCampdata) => [...prevCampdata, newCampaign]);
  };

  return (
    <Router>
      <div className="App">
        <Route exact path="/" component={LandingPage} /> {/* This is your landing page */}
        {/* Navbar is conditionally rendered */}
        <Switch>
          <Route path="/home" component={Navbar} />
          <Route path="/about" component={Navbar} />
          <Route path="/campaigns" component={Navbar} />
          <Route path="/contact" component={Navbar} />
          <Route path="/card" component={Navbar} />
          <Route path="/signup" component={Navbar} />
          <Route path="/login" component={Navbar} />
          <Route path="/AllCampaigns">
            <AllCampaigns campdata={campdata} />
          </Route>
          <ProtectedRoute path="/admin">
            <Admin addCampaign={addCampaign} />
          </ProtectedRoute>
          <ProtectedRoute path="/updatecampaign" component={UpdateCampaign} />
          <Route path="/paymentform" component={PaymentForm} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
