import React, { useState } from "react";
import { TextField, Button } from "@material-ui/core";
import CallIcon from "@material-ui/icons/Call";
import EmailIcon from "@material-ui/icons/Email";

function Contact() {
  // State to hold form data
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const queryData = { name, email, phone, message };

    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:4000/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(queryData),
      });

      if (response.ok) {
        alert("Query submitted successfully!");
        setName("");
        setEmail("");
        setPhone("");
        setMessage("");
      } else {
        const errorMsg = await response.text();
        alert(`Failed to submit the query: ${errorMsg}`);
      }
    } catch (error) {
      console.error("Error submitting the query:", error);
      alert("An error occurred while submitting the query. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div id="contact" className="footer">
      {/* Main Flex Container */}
      <div className="footer-content">
        {/* Online Query Section */}
        <div className="footer-contact-quote online-query-section">
          <h2><b>ONLINE QUERY</b></h2>
          <form onSubmit={handleSubmit}>
            <div className="footer-query">
              <TextField
                label="Name"
                variant="outlined"
                fullWidth
                margin="normal"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <TextField
                label="Email"
                variant="outlined"
                fullWidth
                margin="normal"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <TextField
                label="Phone Number"
                variant="outlined"
                fullWidth
                margin="normal"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
              <TextField
                label="Message"
                variant="outlined"
                fullWidth
                margin="normal"
                multiline
                rows={4}
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                disabled={isLoading}
              >
                {isLoading ? "Submitting..." : "Submit Query"}
              </Button>
            </div>
          </form>
        </div>

        {/* Contact Us Section */}
        <div className="footer-contact-info">
          <h2>CONTACT US</h2>
          <h5><b><CallIcon />: 9573623670 , 6304714584</b></h5>
          <h5><b><EmailIcon />: lokeshbethala11868@gmail.com, bodigavignesh1234@gmail.com</b></h5>
        </div>
      </div>

      {/* Footer Quote */}
      <div className="footer-quote">
        <h1><b>"THEY NEED YOU 🤝"</b></h1>
        <h4><b>"DEVELOPED BY: LOKESH, VIGNESH"</b></h4>
      </div>
    </div>
  );
}

export default Contact;
