import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function Login() {
  const [loginType, setLoginType] = useState("");
  const [credentials, setCredentials] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const history = useHistory();

  const adminCredentials = {
    email: "vignesh05@123",
    password: "vignesh123",
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({
      ...credentials,
      [name]: value,
    });
  };

  const submitCredential = async (e) => {
    e.preventDefault();
    if (loginType === "admin") {
      if (
        credentials.email === adminCredentials.email &&
        credentials.password === adminCredentials.password
      ) {
        localStorage.setItem("user", JSON.stringify(credentials));
        setMessage("Admin login successful!");
        history.push("/admin"); // Redirect to admin page
      } else {
        setMessage("Invalid admin credentials.");
      }
    } else if (loginType === "user") {
      // Handle user login (implement your user validation logic here)
      localStorage.setItem("user", JSON.stringify(credentials));
      setMessage("User login successful!");
      history.push("/"); // Redirect to home page
    }
  };

  return (
    <div className="login-page">
      <h1>Login</h1>
      <div>
        <button onClick={() => setLoginType("admin")}>Admin Login</button>
        <button onClick={() => setLoginType("user")}>User Login</button>
      </div>

      {loginType && (
        <form onSubmit={submitCredential}>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={credentials.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
      )}

      {message && <p>{message}</p>}
    </div>
  );
}

export default Login;
