import React, { useState } from 'react';
import CreateArea from './CreateArea';

function Admin({ addCampaign }) {
  const [campdata, setCampdata] = useState(() => {
    const savedCampdata = localStorage.getItem('campdata');
    return savedCampdata ? JSON.parse(savedCampdata) : [];
  });

  // Handle Logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Add Campaign
  const handleAddCampaign = (newCampaign) => {
    addCampaign(newCampaign); // Add to parent state
    setCampdata((prevCampdata) => [...prevCampdata, newCampaign]); // Update local state
  };

  // Delete Campaign
  const handleDeleteCampaign = (title) => {
    const updatedCampdata = campdata.filter((campaign) => campaign.title !== title);
    setCampdata(updatedCampdata);
    localStorage.setItem('campdata', JSON.stringify(updatedCampdata)); // Update localStorage
  };

  return (
    <div className="admin-container">
      <button
        type="button"
        className="btn btn-danger btn-lg adminLogout"
        onClick={handleLogout}
      >
        LOGOUT
      </button>
      <div className="admin-content">
        <h3 className="AdminAddCampaignTitle">ADD CAMPAIGN</h3>
        <div className="AdminAddCampaignForm Adminform">
          <CreateArea onAdd={handleAddCampaign} />
        </div>

        <h3 className="AdminDeleteCampaignTitle">EXISTING CAMPAIGNS</h3>
        <ul className="campaign-list">
          {campdata.map((campaign, index) => (
            <li key={index} className="campaign-item">
              <div>
                <strong>Title:</strong> {campaign.title}
              </div>
              <div>
                <strong>Description:</strong> {campaign.description}
              </div>
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleDeleteCampaign(campaign.title)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Admin;
