import React from 'react';
import CreateArea from './CreateArea';
import './Admin.css';

function Admin({ addCampaign }) {
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="admin-container">
      <button type="button" className="btn btn-danger btn-lg adminLogout" onClick={handleLogout}>LOGOUT</button>
      <div className="admin-content">
        <h3 className="AdminAddCampaignTitle">ADD CAMPAIGN</h3>
        <div className="AdminAddCampaignForm Adminform">
          <CreateArea onAdd={addCampaign} />
        </div>
      </div>
    </div>
  );
}

export default Admin;
