import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { load } from "@cashfreepayments/cashfree-js";

function PaymentForm() {
  const [cashfree, setCashfree] = useState(null);
  const [note, setNote] = useState({
    name: "",
    email: "",
    phone: "",
    amount: "",
  });
  const [orderId, setOrderId] = useState("");

  useEffect(() => {
    const initializeSDK = async () => {
      const sdk = await load({ mode: "sandbox" });
      setCashfree(sdk);
    };
    initializeSDK();
  }, []); // Empty dependency array ensures this runs only once

  const location = useLocation();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  };

  const getSessionId = async () => {
    try {
      const res = await axios.get("http://localhost:5000/payment");
      if (res.data && res.data.payment_session_id) {
        console.log(res.data);
        setOrderId(res.data.order_id);
        return res.data.payment_session_id;
      } else {
        throw new Error("Invalid session ID response from server.");
      }
    } catch (error) {
      console.error("Error fetching session ID:", error);
      alert("Failed to initialize payment session. Please try again.");
      throw error; // Propagate the error
    }
  };

  const handleClick = async (e) => {
    e.preventDefault();
    try {
      if (!cashfree) {
        throw new Error("Payment SDK is not initialized yet.");
      }

      const sessionId = await getSessionId();
      const checkoutOptions = {
        paymentSessionId: sessionId,
        redirectTarget: "_modal",
      };
      await cashfree.checkout(checkoutOptions);
      console.log("Payment Initialized");
    } catch (error) {
      console.error("Payment Initialization Error:", error);
      alert("Payment initialization failed. Please try again.");
    }
  };

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form onSubmit={handleClick}>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
          required
        />
        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="email"
          required
        />
        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="tel"
          required
        />
        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="number"
          required
        />
        <button type="submit">Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
