import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom"; // Import Link

function Login() {
  const [credential, setCredential] = useState({
    email: "",
    password: "",
    role: "user", // Default role is user
  });
  const [error, setError] = useState(""); // State to store login errors
  const history = useHistory();

  function handleChange(event) {
    const { name, value } = event.target;
    setCredential((prevCredential) => ({
      ...prevCredential,
      [name]: value,
    }));
  }

  function submitCredential(event) {
    event.preventDefault();

    // Reset error state before making the request
    setError("");

    // Send login request to the backend
    axios
      .post("http://localhost:4000/login", credential)
      .then((res) => {
        if (res.data.result === true) {
          // Save user data to localStorage and redirect to respective page
          localStorage.setItem("user", JSON.stringify(res.data));

          // Redirect based on user role
          if (credential.role === "admin") {
            history.push("/admin"); // Redirect to admin page
          } else {
            history.push("/home"); // Redirect to user home page
          }
        } else {
          setError("Invalid credentials. Please try again.");
        }
      })
      .catch((error) => {
        console.error("Login failed: ", error);
        setError("Invalid credentials. Please try again.");
      });
  }

  useEffect(() => {
    // Check if the user is already logged in by checking localStorage
    const user = localStorage.getItem("user");
    if (user) {
      history.push("/home"); // Redirect to home if user is already logged in
    }
  }, [history]);

  return (
    <div className="Login-page">
      <form>
        <label>Email</label>
        <input
          type="email"
          name="email"
          onChange={handleChange}
          value={credential.email}
          required
        />
        <label>Password</label>
        <input
          type="password"
          name="password"
          onChange={handleChange}
          value={credential.password}
          required
        />

        <label>Role</label>
        <select
          name="role"
          value={credential.role}
          onChange={handleChange}
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>

        <button type="submit" onClick={submitCredential}>
          Login
        </button>
      </form>

      {error && <p className="error">{error}</p>} {/* Show error message if any */}
      
      <p>
        Not Registered? <Link to="/signup">Signup</Link>
      </p>
    </div>
  );
}

export default Login;
