import React, { useState } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

function PaymentForm() {
  const location = useLocation();

  const [note, setNote] = useState({
    name: "",
    email: "",
    phone: "",
    amount: "",
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  }

  function submitData(event) {
    event.preventDefault();

    note.title = location.state?.title || "Default Title"; // Fallback if state is missing
    note.content = location.state?.content || "Default Content";

    axios
      .post("http://localhost:4000/paynow", note)
      .then((res) => {
        console.log("Server Response:", res.data);

        const { orderId, amount, currency } = res.data;

        const options = {
          key: "YOUR_RAZORPAY_KEY_ID", // Replace with your Razorpay Key ID
          amount: amount, // Amount in paisa
          currency: currency,
          name: "Your Organization Name",
          description: "Donation Payment",
          order_id: orderId,
          handler: function (response) {
            console.log("Payment Successful:", response);
            alert("Payment Successful!");
          },
          prefill: {
            name: note.name,
            email: note.email,
            contact: note.phone,
          },
          theme: {
            color: "#F37254",
          },
        };

        const rzp = new window.Razorpay(options);
        console.log("Initializing Razorpay...");
        rzp.open(); // Open the Razorpay payment modal
      })
      .catch((error) => console.error("Error:", error));
  }

  return (
    <div className="payment-form">
      <h1>DONATION FORM</h1>
      <form>
        <input
          name="name"
          onChange={handleChange}
          value={note.name}
          placeholder="Name"
          type="text"
        />
        <input
          name="email"
          onChange={handleChange}
          value={note.email}
          placeholder="Email"
          type="text"
        />
        <input
          name="phone"
          onChange={handleChange}
          value={note.phone}
          placeholder="Phone"
          type="text"
        />
        <input
          name="amount"
          onChange={handleChange}
          value={note.amount}
          placeholder="Amount"
          type="text"
        />
        <button onClick={submitData}>Pay</button>
      </form>
    </div>
  );
}

export default PaymentForm;
